import React, { useEffect, useState } from "react";
import CategoryPage from "./CategoryPage";

function Categories(){

    const [catArr, setCatArr] = useState([])

    useEffect(() => {
        fetch("/categories")
        .then(resp => resp.json())
        .then((data) => setCatArr(data))
    },[])

    const catSelect = catArr.map((category) => {
        return(
            <CategoryPage key={category.id} {...category}/>
        )
    })

    console.log(catArr)

    // console.log(catSelect[1])

    return(
        <div className= "flex justify-evenly space-x-2 text-2xl font-medium text-indigo-500 dark:text-gray-100">
            <button>Makeup</button>
            <button>Skin Care</button>
            <button>Hair Care</button>
        </div>  
    )
}

export default Categories
