import React from "react";
import ProductCard from "./ProductCard";

function HomePage({ productsArray }) {

    // console.log(productsArray)
    const productsToDisplay = productsArray.map((product) => {
        return(
            <ProductCard key={product.id} {...product} />
        )
    })

    return (
        <>
        <div style={{ position: 'relative', width: '1000px', height: '400px' }}>
            <img
            src="https://i.ibb.co/D1yL79W/cosmetics-banner2.webp"
            alt="cosmetics-banner2"
            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
            <div
            style={{
                position: 'center',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                color: 'white',
                textAlign: '',
            }}
            >
          <h3>Homepage</h3>
        </div>
      </div>
      {productsToDisplay}
    </>
    )
}

export default HomePage
