import React, { useState, useEffect } from "react";

function App() {

    const [productsArray, setProductsArray] = useState([])

    useEffect(() => {
        fetch("/products")
        .then(resp=>resp.json())
        .then((data)=>{
            // console.log(data)
            setProductsArray(data)
        })
    }, [])

        const productsToDisplay = productsArray.map((product) => {
            return (
                <div className="prodCard">
                    <h1>{product.name}</h1>
                    <h3>{product.brand}</h3>
                </div>
            )
        })


    return (
        <div>
            <h2>Hola (panic)</h2>
            {productsToDisplay}
        </div>
    )
}

export default App;
